#include <iostream>
using namespace std;
#include <vector>
#include <algorithm>
#include <set>

//先排序 然后固定中间 再双指针
//不过对于排异用的是set方法 对于如下特例速度很慢
vector<vector<int>> threeSum(vector<int>& nums) {
	vector<vector<int>> res;
	if (nums.size() <= 2)
	{
		return res;
	}
	set<vector<int>> myset;
	sort(nums.begin(), nums.end());
	
	for (int i = 1; i<nums.size() - 1; i++)
	{
		int le(0), ri(nums.size() - 1);
		while (le<i&&ri>i)
		{
			int mid = nums[i];
			if (nums[le] + nums[ri] + mid == 0)
			{
				vector<int> tmp(3, 0);
				tmp[0] = nums[le];
				tmp[1] = mid;
				tmp[2] = nums[ri];
				myset.insert(tmp);
				le++;
			}
			else if (nums[le] + nums[ri] + mid<0)
			{
				le++;
			}
			else if (nums[le] + nums[ri] + mid>0)
			{
				ri--;
			}
		}
	}
	res.assign(myset.begin(), myset.end());
	return res;
}
