#include <iostream>
using namespace std;
#include <vector>
#include <algorithm>
//学到的：可以直接{ }给vector赋值
//犯的错：nums.end()指向最后一位的后一位
//先排序 每次以（a-第一个值）作为目标 向后搜索另外两个值 如果第一个值已经大于a就返回 搜索另外两个值用双指针
vector<vector<int>> threeSum(vector<int>& nums) {
	vector<vector<int>> res;
	if (nums.size() < 3) return res;
	sort(nums.begin(), nums.end());
	for (vector<int>::iterator it = nums.begin(); it != nums.end() - 2;){
		int tmp = *it;
		if (tmp > 0) break;
		int target = 0 - tmp;
		vector<int>::iterator left = it + 1;
		vector<int>::iterator right = nums.end() - 1;
		while (left < right){
			if (*right < 0) break;
			if (*left + *right < target){
				int v = *left;
				while (left != right && *left == v) left++; //跳过相等的元素
			}
			else if (*left + *right > target){
				int v = *right;
				while (left != right && *right == v) right--;//跳过相等的元素
			}
			else{
				vector<int> tmp_res{ tmp, *left, *right };
				int v = *left;
				while (left != right &&  *left == v) left++;//跳过相等的元素
				v = *right;
				while (left != right &&  *right == v) right--;//跳过相等的元素
			}
		}
		while (it != nums.end() - 2 && *it == tmp) it++;//跳过相等的元素
	}
	return res;
}


